property.value:
const dataBindingMappingTypeId = "ee19a932-0cea-4b90-bd3c-857d7942d390";
const invocationMappingTypeId = "e4f0c63b-0f00-42bd-a703-00adf44f3364";
//const paginationStereotypeId = "f0663cd4-da7c-43cc-9cda-bbc7923d5431";

element.typeReference.setIsNullable(true); // paged models will almost always be nullable.
const dataCollection = element.getChildren().filter(x => x.getName() == "Data")[0];
const table = createElement("Data Grid", "DataGrid", mapping.getTargetElement().id);
//table.addStereotype(paginationStereotypeId);

mapping.addMappedEnd(dataBindingMappingTypeId, dataCollection.getMappingPath(), table.getMappingPath());

const tableRef = table.getChild(x => x.specializationId == "28fb9d1a-cd73-4a5e-ac21-6340ec2e90d0"); // Ref static mappable
const newProperty = createElement("Property", toPascalCase(table.getName()), mapping.getSourceElement().id);
newProperty.typeReference.setType(tableRef.typeReference.toModel());
mapping.addMappedEnd(dataBindingMappingTypeId, newProperty.getMappingPath(), tableRef.getMappingPath());

const totalCount = element.getChildren().filter(x => x.getName() == "TotalCount")[0];
mapping.addMappedEnd(dataBindingMappingTypeId, totalCount.getMappingPath(), table.getMappingPath().concat(["a5e50b44-c402-4006-bcea-a25ab7dc0c56"])); // Total Items

const onPageChangedOperation = createElement("Component Operation", `Fetch${table.getName()}Data`, mapping.getSourceElement().id);
const pageNoParam = createElement("Parameter", "pageNo", onPageChangedOperation.id);
pageNoParam.typeReference.setType("fb0a362d-e9e2-40de-b6ff-5ce8167cbe74"); // int
const pageSizeParam = createElement("Parameter", "pageSize", onPageChangedOperation.id);
pageSizeParam.typeReference.setType("fb0a362d-e9e2-40de-b6ff-5ce8167cbe74"); // int
const sortingParam = createElement("Parameter", "sorting", onPageChangedOperation.id);
mapping.addMappedEnd(invocationMappingTypeId, onPageChangedOperation.getMappingPath(), table.getMappingPath().concat(["53a618ca-e5aa-49ca-a993-8cd935683748"])); // On Load Data
mapping.addMappingExpression(table.getMappingPath().concat(["53a618ca-e5aa-49ca-a993-8cd935683748", pageNoParam.id]), "pageNo"); // On Load Data -> Parameter (synced)
mapping.addMappingExpression(table.getMappingPath().concat(["53a618ca-e5aa-49ca-a993-8cd935683748", pageSizeParam.id]), "pageSize"); // On Load Data-> Parameter (synced)
mapping.addMappingExpression(table.getMappingPath().concat(["53a618ca-e5aa-49ca-a993-8cd935683748", sortingParam.id]), "sorting"); // On Load Data -> Parameter (synced)

dataCollection.getChildren().filter(x => x.specialization == "Property" || x.specialization == "DTO-Field").forEach(p => {
    if (p.typeReference.getTypeId() == "d384db9c-a279-45e1-801e-e4e8099625f2" || // string
        p.typeReference.getTypeId() == "fb0a362d-e9e2-40de-b6ff-5ce8167cbe74" || // int
        p.typeReference.getTypeId() == "1fbaa056-b666-4f25-b8fd-76fe3165acc8" || // date
        p.typeReference.getTypeId() == "a4107c29-7851-4121-9416-cf1236908f1e" || // datetime
        p.typeReference.getTypeId() == "675c7b84-997a-44e0-82b9-cd724c07c9e6") { // decimal
        const newColumn = createElement("Data Grid Column", toTitleCase(p.getName()), table.id);
        mapping.addMappedEnd(dataBindingMappingTypeId, p.getMappingPath(), newColumn.getMappingPath())
    }
    else if (p.typeReference.getTypeId() == "e6f92b09-b2c5-4536-8270-a4d9e5bbd930"){ // bool
        const newColumn = createElement("Data Grid Column", toTitleCase(p.getName()), table.id);
        const newInput = createElement("Checkbox", p.getName(), newColumn.id);
        newInput.removeStereotype("Label Addon");
        mapping.addMappedEnd(dataBindingMappingTypeId, p.getMappingPath(), newInput.getMappingPath());
    }
});
